        console.log(\$scope.form);
    }
    \$scope.typeSelect=function(\$event='Today'){
        if(['Single Date', 'Date Range', 'Single Month', 'Single Year'].indexOf(\$event)>-1){
            \$scope.timeFilter.dateData={ singleDt: \$event=='Single Date'?new Date().getTime():\$event=='Single Month'?new Date(moment().format('YYYY-MM-01')).getTime():\$event=='Single Year'?new Date(moment().format('YYYY-01-01')).getTime():'', startDt: \$event=='Date Range'?moment().subtract(1, 'days').format('YYYY-MM-DD'):'', endDt: \$event=='Date Range'?moment().format('YYYY-MM-DD'):'' };
            setTimeout(function() {
                if(['Single Month', 'Single Year'].includes(\$scope.timeFilter.selectedRangeType)) {
                    \$('#monthOrYear').val(moment(new Date(\$scope.timeFilter.dateData.singleDt)).format(\$scope.selectedType=='Single Month'?'MMM, YYYY':'YYYY'));
                }
            }, 10);
        }
        \$scope.form=solveTypeData(\$event, weekStartDay);
        submitSearch();
    }
    function solveTypeData(type='Today', weekStartDay){
        \$scope.timeFilter.selectedRangeType=type;
        localStorage.setItem('time_filter', JSON.stringify(\$scope.timeFilter));
        return syncross.getTimeData(type, weekStartDay, dayStartTime);
    }
    \$scope.selectMonthOrYear=function(eventData, dp){
        \$scope.timeFilter.dateData.singleDt=new Date(moment(eventData).format(\$scope.selectedType=='Single Month'?'YYYY-MM-01':'YYYY-01-01')).getTime();
        $('#monthOrYear').val(moment(eventData).format(\$scope.selectedType=='Single Month'?'MMM, YYYY':'YYYY'))
        dp.close();
        \$scope.form=solveTypeData(\$scope.timeFilter.selectedRangeType, weekStartDay, dayStartTime);
        submitSearch();
    }
    \$scope.singleDateChange=function(\$event){
        \$scope.form.startDate=moment(\$event).format('YYYY-MM-DD 00:00:00');
        \$scope.form.endDate=moment(\$event).format('YYYY-MM-DD 23:59:59');
        \$scope.timeFilter.dateData.singleDt=\$event.getTime();
        localStorage.setItem('time_filter', JSON.stringify(\$scope.timeFilter));
        submitSearch();
    }
    \$scope.chooseDateRange=function(\$event, pos='start', pk){
        if (\$event) {
            const isStart = pos === 'start';
            \$scope.form[isStart ? 'startDate' : 'endDate'] = moment(\$event).format(`YYYY-MM-DD \${isStart ? '00:00:00' : '23:59:59'}`);
            \$scope.timeFilter.dateData[isStart ? 'startDt' : 'endDt'] = \$event.getTime();
            localStorage.setItem('time_filter', JSON.stringify(\$scope.timeFilter));
            if(!isStart) {
                pk.close();
                submitSearch();
            }
        }
    }
    \$scope.showDateFromTs=function(ts=''){ return ts==''?'':new Date(ts); }
    \$scope.goBack = function() { window.history.back(); }
}
